import Vue from 'vue';
import Router from 'vue-router';
import HelloContainer from "./components/HelloWorld.vue";
//1.引入自定义组件
import HomeContainer from "./components/tabbar/HomeContainer.vue";
import NewsContainer from "./components/news/NewsContainer.vue";
import MemberContainer from "./components/tabbar/MemberContainer";
import SearchContainer from "./components/tabbar/SearchContainer";
import ShopContainer from "./components/tabbar/ShopContainer";
import NewsDetailContainer from "./components/tabbar/NewsDetailContainer";
import comment from "./components/subcomponents/comment.vue";
import goodlist from "./components/goods/GoodList.vue";
import goodsinfo from "./components/goods/GoodsInfo.vue";
import cart from "./components/goods/Cart.vue"

Vue.use(Router)

export default new Router({
  routes: [
    {path:'/',redirect:"/home"},
    {path:'/home',component:HomeContainer},
    {path:'/home/newslist',component:NewsContainer},
    {path:'/member',component:MemberContainer},
    {path:'/search',component:SearchContainer},
    {path:'/shop',component:ShopContainer},
    {path:'/newsdetail/:id',component:NewsDetailContainer},
    {path:'/comment',component:comment},
    {path:'/home/goodlist',component:goodlist},
    {path:'/home/goodsinfo/:id',component:goodsinfo},
    {path:'/home/cart',component:cart}
  ],linkActiveClass:"mui-active"
})
